$(function(){
	//slider
	var swiper=new Swiper(".mySwiper", {
		pagination: {
			el: ".swiper-pagination",
		},
	});
	//banner
	var familyPos=0;
	var bannerWidth=164;

	$(".prev").click(function(e){
		e.preventDefault();
		rightMoving();
	});
	$(".next").click(function(e){
		e.preventDefault();
		leftMoving();
	});
	function leftMoving(){
		familyPos-=bannerWidth;
		$(".site_wrapper ul").animate({left:familyPos}, 500, function(){
			$(this).append($(".site_wrapper ul li:first-child"));
			familyPos+=bannerWidth;
			$(this).css({left:familyPos});
		});
	}
	function rightMoving(){
		$(".site_wrapper ul").prepend($(".site_wrapper ul li:last-child"));
		familyPos-=bannerWidth;
		$(".site_wrapper ul").css({left:familyPos});
		familyPos+=bannerWidth;
		$(".site_wrapper ul").animate({left:familyPos}, 500);
	}
//congra slider
	var n=0;
	var total=3;
	$(".keyvisual li").eq(n).addClass("current");
	$(".pagination li").eq(n).addClass("current");

	$(".pagination li").click(function(e){
		e.preventDefault();
		n=$(this).index();
		$(".pagination li").removeClass("current");
		$(this).addClass("current");
		$(".keyvisual li").removeClass("current");
		$(".keyvisual li").eq(n).addClass("current");
	});
});